local _, addon = ...
if (not addon.hallowsend.IsEventActive()) then return end

local Coords = CreateVector2D
local TCL = addon.hallowsend.TomCatsLibs
local D = TCL.Data
local quests = { }
local faction = UnitFactionGroup("player")
local factionID = faction == "Alliance" and 1 or 2
for k, v in ipairs(D["Locations"].records) do
	local row = { }
	if (v.record[3] == factionID or v.record[3] == 3) then
		row[1] = v.record[2]
		row[4] = v.record[4]
		row[6] = Coords(v.record[5][1], v.record[5][2])
		table.insert(quests, row)
	end
end

select(2, ...).hallowsend.TomCatsLibs.Data.loadData(
	"Quests",
	{ "Quest ID", "Creature ID", "Area ID", "UIMap ID", "Map Type", "Location" },
	quests
)

